INSERT INTO `issue_main` VALUES ('10788', null, '485', '10002', 'chigo', 'chigo', 'chigo', '1', '订单查询的买家秀入口', '订单查询的写买家秀入口只出现在成品订单（女戒和对戒）待收货和已完成的两个状态，其他状态是我们还没有发货，不存在的。\n【正常业务逻辑是营业员给消费者看商品详情的买家秀信息引导购买，下单完成后，提交到我们的订单管理后台，我们把商品加工好了给门店发货即后台是已发货状态，这时对应的平板APP的状态就是待收货状态，还有已完成的两个状态才有写买家秀评论的入口。所以在订单查询的写买家秀的入口只出现成品订单（女戒和对戒）的待收货和已完成两个状态。】\n另外的买家秀现在只有文字链接，应改成图标链接。', null, '3', '1', '6', '2016-03-08 16:45:30', '2016-06-13 11:57:28', null, '2016-03-10 09:45:17', '0', '1', null, null, null, '10988', null, null, null);
