INSERT INTO `issue_main` VALUES ('10812', null, '490', '10002', 'zhhong', 'fzj01571', 'zhhong', '3', 'APP价格尾数显示规则。', '1、戒指商品详情页的总价、填写订单页的总价按价格尾数处理。（后台接口返回处理过的总价给APP）\n2、价格尾数只对钻戒的（包括定制女戒、对戒、单买对戒）总价有效，裸钻价格不用处理价格尾数。\n3、导购修改价格时，价格尾数失效。\n\n说明：\n这个规则我们会在帮助文档里进行说明，同时也会告知导购。\n如果消费者有疑问则导购可以向消费者说明这个价格是系统自动按照吉利数字进行了处理。\n这个价格尾数处理不影响测试和数据统计。', null, '3', '1', '6', '2016-03-09 18:48:33', '2016-06-13 11:55:52', null, '2016-03-16 09:30:27', '0', '2', null, null, null, '11012', null, null, null);
