INSERT INTO `issue_main` VALUES ('11300', null, '345', '10009', 'zhhong', 'dhlin', 'zhhong', '3', '开美宝隆二级细节变更', '主要三点：\n1、开美宝隆二级时，客户主任统一改成“丁双双”\n2、如果销售助理没提供客户倍率则先不为其开通导购账号。\n3、进美宝隆，把刚创建会员里销售员改成实际的销售助理。', null, '3', '1', '5', '2016-05-03 19:36:03', '2016-05-12 09:31:53', null, '2016-05-12 09:31:53', '0', '1', null, null, null, '11536', null, null, null);
INSERT INTO `issue_main` VALUES ('11301', null, '101', '10102', 'zhhong', 'dhlin', 'zhhong', '3', '创建团队账号', '重新规划团队账号\n1、为商品部、销售助理、财务部、物流部重新创建账号。\n账号名：以岗位的英文来命名。一个岗位有多个人时则在后面加数字作区别。\n姓名：真实姓名\n手机号：真实手机号。\n2、把会员重新划分到新的销售助理账号下。\n3、通知相关人员使用新的账号登录系统并修改密码。\n4、删除老的账号。\n', null, '3', '1', '5', '2016-05-04 10:44:55', '2016-05-04 15:45:08', '2016-05-04 00:00:00', '2016-05-04 15:45:08', '0', '1', null, null, null, '11537', null, null, null);
