INSERT INTO `issue_main` VALUES ('12150', null, '282', '10110', 'rzhu', 'hwye', 'rzhu', '1', '入库订单列表 -删除', '1、删除：点击 【删除】按钮， \nA、检查订单状态，只有“未审核”“审核不通过”的订单才能删除，不满足条件的弹框提醒；\nB、首先根据 明细表条码 删除 商品表，成本明细表，石料表等数据，物理删除\nC、然后，删除明细表所有数据；\nD、最后，入库主表对应入库订单号物理删除；\n//需求变更2016-08-22\n2、搜索按钮：\n（1）客户和供应商控件，要根据当前登录用户过滤客户权限；（控件处理）\n（2）如果客户和供应商控件为空的情况，也只能搜索用户客户权限内的所有客户数据；（列表页面搜索事件处理）\n所有的页面都要过滤用户的客户和供应商权限；\n', null, '3', '1', '6', '2016-09-01 17:47:17', '2016-09-12 15:14:31', null, '2016-09-10 15:36:40', '0', '3', null, null, null, '12386', null, null, null);
