INSERT INTO `issue_main` VALUES ('12171', null, '120', '10102', 'xffang', 'lvpengdong', 'xffang', '3', '诺戒商品上新需求', '1.功能描述\n采用金工石卖现货模式\n(1)后台提供诺戒商品现货款式添加管理页面\n(2)开发接口给前端APP定制产品取数；\n(3)前端下单采用金工石明细公式计算售价；\n\nAPP参数（手寸，材质，镶口范围）给服务器，服务器到数据库搜索库存条码推送一个最早入库的条码，然后计算出售价回传给APP显示；\n计算规则：\n损耗：定制款式维护传值；\n取全局金价和 商品表金重、主石额，辅石额，工费\n二级后台：取 诺戒 销售倍率,诺戒没有就取统一倍率\n计算公式：\n售价=（金价*金重*（1+损耗）+主石额+辅石额+工费）*倍率\n\n', null, '3', '1', '6', '2016-09-02 10:35:29', '2016-11-14 14:09:24', null, '2016-11-14 14:09:24', '0', '1', null, null, null, '12407', null, null, null);
