INSERT INTO `issue_main` VALUES ('13781', null, '542', '10110', 'xffang', 'jhuang', 'xffang', '1', '出货-新增/编辑', '一、新增编辑页面\n1、辅石数：不允许编辑\n2、优惠金额计算错误；\nA   主石重<0.3      优惠金额=销售价-销售价*折扣率（四舍五入保留两位小数位数）\nB   主石重>=0.3   优惠金额=空托金额-空托金额*折扣率（四舍五入保留两位小数位数）  出货订单：F2017022800002\n3、页面上“折扣”全部改成“折扣率”\n', null, '3', '1', '5', '2017-02-28 16:09:21', '2017-03-01 19:38:27', null, '2017-03-01 19:38:27', '0', '2', null, null, null, '13981', null, null, null);
