INSERT INTO `issue_main` VALUES ('14185', null, '223', '10400', 'zhhong', 'fzj01571', 'zhhong', '4', '库存管理：上传RAPNET规则优化', '点击上传RAPNET时的数据必须同时满足以下三个条件：\n1、库存状态：回货中/在库/外借\n2、销售状态：在售/外借/保留\n3、上架状态：上架\n\n上传到RAPNET后，数据在RAPNET上的状态字段显示规则\n销售状态为外借和保留的则在RAPNET上显示成MEMO，在售的显示为空。\n\n（分给泽彪）', null, '1', '1', '6', '2017-04-06 15:14:08', '2017-04-12 10:39:02', null, '2017-04-06 16:27:31', '0', '1', null, '0', '3600', '14385', null, null, null);
