INSERT INTO `issue_main` VALUES ('15824', null, '325', '10400', 'ssliu', 'jhuang', 'ssliu', '1', '数据分析--报表分析---供应商分析--库存占比计算错误，正确的计算方式为：统计区间内的销售数量/统计区间内的平均库存量', '数据分析--报表分析---供应商分析--库存占比计算错误，正确的计算方式为：统计区间内的销售数量/统计区间内的平均库存量。\n例如：统计7-10至7-16号的库存占比，7-10至16号的销售量为202，平均库存量为：7-10至16号每日库存量和/统计天数（7天），统计区间内如果 有一天库量为0，则视为这天为无效数据，例如：7-12库存量为0，那么当天不参与计算，即，7-10至16号的库存量和/有效统计天数(6天)。', null, '3', '1', '6', '2017-07-17 11:54:59', '2017-07-18 16:22:08', '2017-07-17 00:00:00', '2017-07-17 17:23:01', '0', '2', null, null, null, '16024', null, null, null);
