INSERT INTO `issue_main` VALUES ('16772', null, '595', '10404', 'jbwu', 'pawu', 'jbwu', '1', '服务号H5页面里的日期和冒号规范', '1、日期规范\n举例：2017-09-01 16:50:55\n调整为：2017.9.1 16:50:55\n日期中多余的0去掉（注意时分秒都是两位，只是针对日期处理），日期用.来代替-，目的省空间，在手机端看起来没有那么拥挤。\n\n涉及的页面有：\n我的订单、订单详情、会员卡\n\n2、冒号规范\n中文符号的冒号统一使用微软雅黑字体的效果，这样能让冒号在前后的间距看起来一致，更加美观。\n\n涉及的页面有：\n订单详情、会员卡\n\nPS：H5的页面都按照这个标准（包含但不限于服务号、企业号端），特殊情况外。', null, '3', '1', '5', '2017-09-04 14:58:23', '2017-09-04 17:12:03', null, '2017-09-04 17:12:03', '0', '2', null, null, null, '16972', null, null, null);
